import React, { Component } from 'react';
import './App.css';
import NaviBar from './Components/NavBar';
import { Jumbotron, Button, Carousel, Grid, Row, Col, Label} from 'react-bootstrap';
import GameSlides from './Components/GameSlides';
import Amplify from 'aws-amplify';
import awsmobile from './aws-exports';
import { withAuthenticator } from 'aws-amplify-react';
Amplify.configure(awsmobile); 

class Home extends Component {
  
  constructor(props) {  

    super(props);  

    this.state = { response: "" };  

    fetch('http://localhost:5002/api/v1/infrastructure/info')  

        .then(response => response.json())  

        .then(data => {  

            this.setState({ response: data });  

        });
  }

  render() {
    return (
      <div className="App">
        <NaviBar/>
        <Jumbotron className ="Jumbo">
          <h1 className = "text"> Knights Arcade</h1>
          <p className = "text">
            Play / Share / Showcase
          </p>
          <p>
            <Button bsStyle="primary">Find An Arcade Machine</Button>
          </p>
        </Jumbotron>

        <GameSlides/>
        <Label>{this.state.response + " boo"}</Label>
      </div>
    )
  }
}

export default withAuthenticator(Home, true);
